import React from 'react';
import styled from 'styled-components';
import { genres } from '../data';

const Container = styled.div`
  width: 1230px;
  margin: auto;
  margin-top: 10px;
  background-color: white;
`;
const TitleContainer = styled.div`
  display: flex;
  align-items: center;
`;
const Title = styled.div`
  margin: 15px;
  font-size: 25px;
`;
const GenresContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;

`;
const IteamContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;
const Image = styled.img`
  width: 150px;
`;
const Name = styled.p`
  font-size: 20px;
  margin-top: 10px;
`;

const Genres = () => {
  return (
    <Container>
      <TitleContainer>
        <Title>Book Genres</Title>
      </TitleContainer>
      <hr />
      <GenresContainer>
        {genres.map((item) => (
          <IteamContainer key={item.id}>
            <Image src={item.image} />
            <Name>{item.name}</Name>
          </IteamContainer>
        ))}
      </GenresContainer>
    </Container>
  );
};

export default Genres;
