import React from 'react';
import styled from 'styled-components';
import { books } from '../data';

const Container = styled.div`
  width: 1230px;
  margin: auto;
  margin-top: 10px;
  background-color: white;
`;
const TitleContainer = styled.div`
  display: flex;
  align-items: center;
  background-color: #f9ceb8;
`;
const Title = styled.div`
  margin: 15px;
  font-size: 25px;
`;
const BooksContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  margin: 10px 20px;
  margin: auto;
  margin: 10px 0px;
`;
const ItemContainer = styled.div`
  width: 250px;
  height: 350px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  padding: 10px 0px;
  margin-bottom: 10px;
  margin-left: 10px;
  border: 0.5px solid lightgray;
  :hover {
    cursor: pointer;
    background-color: aliceblue;
  }
`;
const Image = styled.img`
  width: 90%;
`;
const Name = styled.p`
  font-size: 17px;
`;
const Price = styled.h3`
  font-size: 24px;
  color: #bd1a1a;
`;
const Button = styled.button`
  padding: 10px 30px;
  background-color: white;
  color: #bd1a1a;
  font-weight: 500;
  font-size: 24px;
    cursor: pointer;
    background-color: #bd1a1a;
    color: white;
  }
`;

const Books = () => {
  return (
    <>
      <Container>
        <TitleContainer>
          <Title>Best Selling Books</Title>
        </TitleContainer>
        <hr />
        <BooksContainer>
          {books.map((item) => (
            <ItemContainer>
              <Image src={item.image} />
              <Name>{item.name}</Name>
              <Price>{item.price}</Price>
            </ItemContainer>
          ))}
        </BooksContainer>
      </Container>

      <Container>
        <TitleContainer style={{ backgroundColor: '#b1d5b1' }}>
          <Title>All Books</Title>
        </TitleContainer>
        <hr />
        <BooksContainer>
          {books.map((item) => (
            <ItemContainer>
              <Image src={item.image} />
              <Name>{item.name}</Name>
              <Price>{item.price}</Price>
            </ItemContainer>
          ))}
        </BooksContainer>
        <Button>SEE MORE</Button>
      </Container>
    </>
  );
};

export default Books;
